  `quote` varchar(255) DEFAULT NULL,
  `status` int(11) DEFAULT '1' COMMENT 'trạng thái hoạt động',
  `remember_token` varchar(255) DEFAULT NULL,
  `verified_at` timestamp NULL DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

